import React from 'react';
import { FaArrowRight } from 'react-icons/fa';
import './Hero.css';

const Hero = () => {
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      const headerHeight = 80;
      const targetPosition = element.offsetTop - headerHeight;
      window.scrollTo({
        top: targetPosition,
        behavior: 'smooth'
      });
    }
  };

  return (
    <section className="hero" id="hero">
      <div className="hero-overlay"></div>
      <div className="container">
        <div className="hero-content">
          <div className="hero-badge">Seit 2003 in Hannover & Region</div>
          <h1 className="hero-title">
            Zielgruppengenaue Zustellung<br />
            an Private Haushalte
          </h1>
          <p className="hero-subtitle">
            Wir bringen Ihre Werbebotschaft direkt in die Briefkästen Ihrer Zielgruppe –<br />
            pünktlich, flächendeckend und mit höchster Sorgfalt.
          </p>
          <div className="hero-cta">
            <button 
              className="btn btn-primary btn-large"
              onClick={() => scrollToSection('kontakt')}
            >
              Jetzt Angebot einholen <FaArrowRight />
            </button>
            <button 
              className="btn btn-secondary btn-large"
              onClick={() => scrollToSection('leistungen')}
            >
              Mehr erfahren
            </button>
          </div>
        </div>
      </div>
      <div className="hero-scroll-indicator">
        <div className="scroll-arrow"></div>
      </div>
    </section>
  );
};

export default Hero;
